KISSY.add(function(a){a.Event.delegate(document,"click",".page-lnk",function(e){e.preventDefault();var d=a.one(e.currentTarget),g=d.attr("data-id")||d.attr("id"),c=d.attr("data-page"),f=d.attr("title"),b=d.attr("data-href");if(top.pageManger){if(c){top.pageManger.redirectPage("",c,true)}else{top.pageManger.openPage("",g,f,b)}}});a.Event.delegate(document,"click",".direct-lnk",function(d){var c=a.one(d.currentTarget),e=c.attr("title"),b=c.attr("href");if(top.pageManger){top.pageManger.setPageTitle(e);location.href=b}})},{requires:["core"]});
